mapper.select:
SELECT GR.GRADE_NAME AS "grName"
FROM MM INNER JOIN GR ON MM.GRADE_CODE = GR.GRADE_CODE
WHERE ID = #{apId}

mapper.update:

UPDATE AL
SET APPROVAL_SEQ_CHECK='C', SIGN_LOCATION=#{signLoc} 
WHERE APPROVAL_ID=#{apId}
AND DOCUMENT_NUM=#{dmNum}


UPDATE AL
SET APPROVAL_SEQ_CHECK='C', SIGN_LOCATION=#{signLoc}  
WHERE APPROVAL_ID=#{apId}
AND DOCUMENT_NUM=#{dmNum}


UPDATE DM
SET APPROVAL_CODE='C'
WHERE DOCUMENT_NUM=#{dmNum}

UPDATE DM
SET APPROVAL_CODE='R'
WHERE DOCUMENT_NUM=#{dmNum}
